$(function(){let height=$(window).height();setTimeout(function(){$(".top-wrapper").css("height",90+"vh");$(".footer").css("height",10+"vh");$(".welcome").css("padding-top",height/3.5);$(".top-wrapper").animate({width:"toggle"},500);$(".hello").fadeIn(3000)});setTimeout(function(){$(".header").slideDown(1000)},3000);setTimeout(function(){$(".profile,.works,.service,.contact").fadeIn(1000)},4000);$(".header-list li").hover(function(){let text=$(this).text();$(".port-folio").text("show");$(".name").text(text)},function(){$(".port-folio").text("WELCOME TO MY PORTFOLIO!");$(".name").text("Sougo Ito")});$(".service-icon,.return-btn").hover(function(){$(this).css("color","white").css("background-color","black")},function(){$(this).css("color","black").css("background-color","white")});$(window).scroll(function(){if($(this).scrollTop()>height-100){$(".return-btn").fadeIn()}else{$(".return-btn").fadeOut()}let profile=$("#profile").offset().top;if($(this).scrollTop()>=profile/1.5){$(".profile-text p").slideDown()}let skill=$(".skill").offset().top;if($(this).scrollTop()>=skill/1.5){$(".skill-list li").slideDown()}let works=$(".works").offset().top;if($(this).scrollTop()>=works/1.5){$(".work-detail").css({bottom:"-500px",opacity:"0"}).animate({bottom:"500px",opacity:"1"},2000)}let service=$(".service").offset().bottom;if($(this).scrollTop()>=service){$(".service-text p").fadeIn()}});$(".return-btn,.return-top").click(function(){$("body,html").animate({scrollTop:0},500)});$(".header-list a,.nav-list a").click(function(){let id=$(this).attr("href");let position=$(id).offset().top;$("html,body").animate({scrollTop:position},500)});if(window.matchMedia("(min-width: 481px) and (min-width: 768px)").matches){setTimeout(function(){$(".top-wrapper").css("height",90+"vh")})}});